mapper.insert:
	    INSERT INTO CUSTOM (
			ID
			, ADDRESS
			, EMAIL
			, NAME
			, PASS
            , PHONE1
            , PHONE2
            , PHONE3
		)
		values(
			#{ID}
			, #{ADDRESS}
			, #{EMAIL}
			, #{NAME}
			, #{PASS}
            , #{PHONE1}
            , #{PHONE2}
            , #{PHONE3}
		)

mapper.select:
select ID,PASS
from CUSTOM
where ID = #{ID}

select PASS
from CUSTOM
where PASS = #{PASS}
